const state = {
  places: [],
};

const getters = {
  places: (state) => state.places,
};

const mutations = {
  setPlaces: (state, places) => {
    state.places = places;
  },
  addPlace: (state, place) => {
    state.places.push(place);
  },
  deletePlace: (state, deletePlace) => {
    state.places = state.places.filter((place) => {
      return place.id != deletePlace.id;
    });
  },
};

